import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function FileUpload() {
  const { projectId, milestoneId } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage(""); // clear message when selecting new file
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      setMessage("‚ö† Please choose a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file); // matches @RequestParam("file")
    formData.append("projectId", projectId); // matches @RequestParam("projectId")

    setUploading(true);
    setMessage("");

    try {
      const res = await axios.post(
        "http://localhost:8080/files/upload",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(`‚úÖ File "${res.data.fileName}" uploaded successfully!`);
      setTimeout(() => {
        navigate(`/projects/${projectId}/milestones`);
      }, 1500);
    } catch (err) {
      console.error("Upload failed:", err);
      if (err.response?.data?.message) {
        setMessage(`‚ùå ${err.response.data.message}`);
      } else {
        setMessage("‚ùå Failed to upload file. Please try again.");
      }
    } finally {
      setUploading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Upload File for Project {projectId}</h2>

      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        {file && <p style={{ marginTop: "5px" }}>üìÑ Selected: {file.name}</p>}

        <button
          type="submit"
          disabled={uploading}
          style={{
            marginTop: "10px",
            padding: "5px 10px",
            backgroundColor: uploading ? "#6c757d" : "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: uploading ? "not-allowed" : "pointer",
          }}
        >
          {uploading ? "Uploading..." : "Upload"}
        </button>
      </form>

      {message && <p style={{ marginTop: "10px" }}>{message}</p>}
    </div>
  );
}
