import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function Milestones() {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const [milestones, setMilestones] = useState([]);
  const [contractId, setContractId] = useState(null);
  const [contractStatus, setContractStatus] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("PENDING");
  const token = localStorage.getItem("token");

  // Fetch milestones for the project
  useEffect(() => {
    fetchMilestones();
  }, [projectId, token]);

  const fetchMilestones = () => {
    axios
      .get(`http://localhost:8080/milestones/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setMilestones(res.data))
      .catch((err) => console.error(err));
  };

  // Fetch contract for this project
  useEffect(() => {
    axios
      .get(`http://localhost:8080/contracts/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        if (res.data) {
          setContractId(res.data.id);
          setContractStatus(res.data.status);
        }
      })
      .catch((err) => console.error(err));
  }, [projectId, token]);

  // Update contract status
  const handleContractStatusChange = (newStatus) => {
    setContractStatus(newStatus);
    if (!contractId) return;
    axios
      .put(
        `http://localhost:8080/contracts/${contractId}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .catch((err) => console.error(err));
  };

  // Update milestone status
  const handleMilestoneStatusChange = (milestoneId, newStatus) => {
    axios
      .put(
        `http://localhost:8080/milestones/${milestoneId}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then(() => {
        setMilestones((prev) =>
          prev.map((m) =>
            m.id === milestoneId ? { ...m, status: newStatus } : m
          )
        );
      })
      .catch((err) => console.error(err));
  };

  // Add a new milestone
  const handleAddMilestone = (e) => {
    e.preventDefault();
    if (!contractId) {
      alert("No contract found for this project. Cannot add milestone.");
      return;
    }
    axios
      .post(
        "http://localhost:8080/milestones",
        { contractId, description, status },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => {
        setMilestones([...milestones, res.data]);
        setDescription("");
        setStatus("PENDING");
        setShowForm(false);
      })
      .catch((err) => console.error(err));
  };

  // Delete a milestone
  const handleDeleteMilestone = (id) => {
    if (!window.confirm("Are you sure you want to delete this milestone?"))
      return;
    axios
      .delete(`http://localhost:8080/milestones/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        setMilestones((prev) => prev.filter((m) => m.id !== id));
      })
      .catch((err) => console.error(err));
  };

  // Delete a milestone's file
  const handleDeleteFile = (milestoneId) => {
    axios
      .delete(`http://localhost:8080/milestones/${milestoneId}/file`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        setMilestones((prev) =>
          prev.map((m) => (m.id === milestoneId ? { ...m, fileName: null } : m))
        );
      })
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Milestones for Project {projectId}</h2>

      {contractStatus && (
        <div style={{ marginBottom: "20px" }}>
          <label style={{ fontWeight: "bold" }}>Contract Status: </label>
          <select
            value={contractStatus}
            onChange={(e) => handleContractStatusChange(e.target.value)}
          >
            <option value="PENDING">Pending</option>
            <option value="COMPLETED">Completed</option>
          </select>
        </div>
      )}

      <button
        onClick={() => setShowForm(!showForm)}
        style={{
          marginBottom: "15px",
          padding: "5px 10px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        {showForm ? "Cancel" : "Add Milestone"}
      </button>

      {showForm && (
        <form onSubmit={handleAddMilestone} style={{ marginBottom: "20px" }}>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            style={{ width: "100%", height: "60px" }}
          />
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            style={{ width: "100%" }}
          >
            <option value="PENDING">Pending</option>
            <option value="COMPLETED">Completed</option>
          </select>
          <button
            type="submit"
            style={{
              padding: "5px 10px",
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Save Milestone
          </button>
        </form>
      )}

      {milestones.length === 0 ? (
        <p>No milestones yet.</p>
      ) : (
        <div style={{ display: "grid", gap: "15px" }}>
          {milestones.map((m) => (
            <div
              key={m.id}
              style={{
                padding: "15px",
                border: "1px solid #ddd",
                borderRadius: "8px",
                background: "white",
              }}
            >
              <p>{m.description}</p>

              {/* Milestone status dropdown */}
              <div style={{ marginTop: "8px" }}>
                <label><strong>Status: </strong></label>
                <select
                  value={m.status}
                  onChange={(e) =>
                    handleMilestoneStatusChange(m.id, e.target.value)
                  }
                >
                  <option value="PENDING">Pending</option>
                  <option value="COMPLETED">Completed</option>
                </select>
              </div>

              <button
                onClick={() =>
                  navigate(`/projects/${projectId}/milestones/${m.id}/upload`)
                }
                style={{
                  marginTop: "10px",
                  padding: "5px 10px",
                  backgroundColor: "#17a2b8",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  width: "100%",
                }}
              >
                Upload File
              </button>

              {m.fileName && (
                <button
                  onClick={() => handleDeleteFile(m.id)}
                  style={{
                    marginTop: "8px",
                    padding: "5px 10px",
                    backgroundColor: "#dc3545",
                    color: "white",
                    border: "none",
                    borderRadius: "4px",
                    cursor: "pointer",
                    width: "100%",
                  }}
                >
                  Delete File
                </button>
              )}

              <button
                onClick={() => handleDeleteMilestone(m.id)}
                style={{
                  marginTop: "8px",
                  padding: "5px 10px",
                  backgroundColor: "#ff4d4f",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  width: "100%",
                }}
              >
                Delete Milestone
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
