import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function Milestones() {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const [milestones, setMilestones] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("PENDING");
  const [contract, setContract] = useState(null);
  const token = localStorage.getItem("token");

  // Fetch milestones
  useEffect(() => {
    axios
      .get(`http://localhost:8080/milestones/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setMilestones(res.data))
      .catch((err) => console.error(err));
  }, [projectId, token]);

  // Fetch contract
  useEffect(() => {
    axios
      .get(`http://localhost:8080/contracts/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        if (res.data) {
          setContract(res.data);
        }
      })
      .catch((err) => console.error(err));
  }, [projectId, token]);

  const handleAddMilestone = (e) => {
    e.preventDefault();
    if (!contract || !contract.id) {
      alert("No contract found for this project.");
      return;
    }

    axios
      .post(
        "http://localhost:8080/milestones",
        {
          contractId: contract.id,
          description,
          status,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => {
        setMilestones([...milestones, res.data]);
        setDescription("");
        setStatus("PENDING");
        setShowForm(false);
      })
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Milestones for Project {projectId}</h2>

      {/* ✅ Contract Details */}
      {contract ? (
        <div
          style={{
            padding: "15px",
            border: "1px solid #ddd",
            borderRadius: "8px",
            marginBottom: "20px",
            backgroundColor: "#f8f9fa",
          }}
        >
          <h3>Current Contract</h3>
          <p><strong>Contract ID:</strong> {contract.id}</p>
          <p><strong>Freelancer:</strong> {contract.freelancerName}</p>
          <p><strong>Budget:</strong> ${contract.amount}</p>
          <p><strong>Status:</strong> {contract.status}</p>
        </div>
      ) : (
        <p style={{ color: "red" }}>No active contract for this project.</p>
      )}

      <button
        onClick={() => setShowForm(!showForm)}
        style={{
          marginBottom: "15px",
          padding: "5px 10px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        {showForm ? "Cancel" : "Add Milestone"}
      </button>

      {showForm && (
        <form
          onSubmit={handleAddMilestone}
          style={{
            marginBottom: "20px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
            background: "#f9f9f9",
          }}
        >
          <div style={{ marginBottom: "10px" }}>
            <label>Description:</label>
            <br />
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              style={{ width: "100%", height: "60px" }}
            />
          </div>
          <div style={{ marginBottom: "10px" }}>
            <label>Status:</label>
            <br />
            <select
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              style={{ width: "100%" }}
            >
              <option value="PENDING">Pending</option>
              <option value="COMPLETED">Completed</option>
            </select>
          </div>
          <button
            type="submit"
            style={{
              padding: "5px 10px",
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Save Milestone
          </button>
        </form>
      )}

      {milestones.length === 0 ? (
        <p>No milestones yet.</p>
      ) : (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
            gap: "15px",
          }}
        >
          {milestones.map((m) => (
            <div
              key={m.id}
              style={{
                padding: "15px",
                border: "1px solid #ddd",
                borderRadius: "8px",
                background: "white",
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              }}
            >
              <p style={{ margin: "0 0 10px" }}>{m.description}</p>
              <span
                style={{
                  padding: "4px 8px",
                  borderRadius: "4px",
                  fontSize: "12px",
                  color: "white",
                  backgroundColor:
                    m.status === "COMPLETED" ? "#28a745" : "#ffc107",
                }}
              >
                {m.status}
              </span>

              <button
                style={{
                  marginTop: "10px",
                  padding: "5px 10px",
                  backgroundColor: "#17a2b8",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  width: "100%",
                }}
                onClick={() =>
                  navigate(`/projects/${projectId}/milestones/${m.id}/upload`)
                }
              >
                Upload File
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
