import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function ManageUsers() {
  const [clients, setClients] = useState([]);
  const [freelancers, setFreelancers] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); // For redirection

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/auth/users");
        const allUsers = res.data;

        setClients(allUsers.filter(user => user.role.toUpperCase() === "CLIENT"));
        setFreelancers(allUsers.filter(user => user.role.toUpperCase() === "FREELANCER"));
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  if (loading)
    return <p style={{ textAlign: "center", marginTop: "40px" }}>Loading users...</p>;

  const handleViewProfile = (userId) => {
    // Redirect to VisitProfile page with userId
    navigate(`/visitprofile/${userId}`);
  };

  const renderUserList = (users) =>
    users.length === 0 ? (
      <p style={{ color: "#666" }}>No users found.</p>
    ) : (
      <div style={styles.userList}>
        {users.map(user => (
          <div key={user.id} style={styles.userCard}>
            <p style={styles.userName}>{user.name}</p>
            <p style={styles.userEmail}>{user.email}</p>
            <p style={styles.userRole}>{user.role}</p>
            <button
              style={styles.viewProfileButton}
              onClick={() => handleViewProfile(user.id)}
            >
              View Profile
            </button>
          </div>
        ))}
      </div>
    );

  return (
    <div style={styles.container}>
      <h1 style={styles.sectionTitle}>Clients</h1>
      {renderUserList(clients)}

      <h1 style={{ ...styles.sectionTitle, marginTop: "40px" }}>Freelancers</h1>
      {renderUserList(freelancers)}
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "900px",
    margin: "40px auto",
    padding: "20px",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  sectionTitle: {
    fontSize: "24px",
    color: "#333",
    borderBottom: "2px solid #007BFF",
    paddingBottom: "6px",
  },
  userList: {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
    gap: "20px",
    marginTop: "20px",
  },
  userCard: {
    background: "#fff",
    padding: "15px",
    borderRadius: "10px",
    boxShadow: "0 6px 15px rgba(0,0,0,0.08)",
    transition: "transform 0.2s, box-shadow 0.2s",
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
  },
  userName: {
    fontWeight: "600",
    color: "#007BFF",
    marginBottom: "5px",
  },
  userEmail: {
    color: "#555",
    fontSize: "14px",
    marginBottom: "5px",
  },
  userRole: {
    color: "#777",
    fontSize: "12px",
    marginBottom: "10px",
  },
  viewProfileButton: {
    padding: "6px 12px",
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "14px",
  },
};
