import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function VisitProfile() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [count, setCount] = useState(0); // total projects or contracts

  useEffect(() => {
    fetchUser();
  }, [id]);

  const fetchUser = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`http://localhost:8080/api/auth/users/${id}`);
      const fetchedUser = res.data;
      setUser(fetchedUser);

      // fetch additional info based on role
      if (fetchedUser.role.toUpperCase() === "CLIENT") {
        const projectsRes = await axios.get("http://localhost:8080/projects");
        const totalProjects = projectsRes.data.filter(p => p.clientId === fetchedUser.id).length;
        setCount(totalProjects);
      } else if (fetchedUser.role.toUpperCase() === "FREELANCER") {
        const contractsRes = await axios.get("http://localhost:8080/contracts");
        const totalContracts = contractsRes.data.filter(c => c.freelancerId === fetchedUser.id).length;
        setCount(totalContracts);
      }

    } catch (err) {
      console.error("Failed to fetch user:", err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p style={{ textAlign: "center" }}>Loading profile...</p>;
  if (!user) return <p style={{ textAlign: "center", color: "red" }}>User not found</p>;

  return (
    <div style={styles.container}>
      <button style={styles.backButton} onClick={() => navigate(-1)}>â¬… Back</button>
      <h1 style={styles.title}>Profile Details</h1>
      <div style={styles.profileCard}>
        <p><strong>Name:</strong> {user.name}</p>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Role:</strong> {user.role}</p>
        {user.role.toUpperCase() === "CLIENT" && <p><strong>Total Projects:</strong> {count}</p>}
        {user.role.toUpperCase() === "FREELANCER" && <p><strong>Total Contracts:</strong> {count}</p>}
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "600px",
    margin: "40px auto",
    padding: "20px",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  title: {
    fontSize: "28px",
    marginBottom: "20px",
    color: "#007BFF",
    textAlign: "center",
  },
  profileCard: {
    background: "#fff",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 6px 15px rgba(0,0,0,0.1)",
    fontSize: "16px",
    lineHeight: "1.6",
  },
  backButton: {
    marginBottom: "20px",
    padding: "8px 15px",
    background: "#6c757d",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    cursor: "pointer",
  },
};
