import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function Milestones() {
  const { projectId } = useParams();
  const navigate = useNavigate();
  const [milestones, setMilestones] = useState([]);
  const [contractId, setContractId] = useState(null);
  const [contractStatus, setContractStatus] = useState("");
  const [showForm, setShowForm] = useState(false);
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("PENDING");
  const token = localStorage.getItem("token");

  // Fetch milestones
  useEffect(() => {
    fetchMilestones();
  }, [projectId, token]);

  const fetchMilestones = () => {
    axios
      .get(`http://localhost:8080/milestones/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setMilestones(res.data))
      .catch((err) => console.error(err));
  };

  // Fetch contract details
  useEffect(() => {
    axios
      .get(`http://localhost:8080/contracts/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        if (res.data) {
          setContractId(res.data.id);
          setContractStatus(res.data.status);
        }
      })
      .catch((err) => console.error(err));
  }, [projectId, token]);

  // Update contract status
  const handleContractStatusChange = (newStatus) => {
    setContractStatus(newStatus);
    if (!contractId) return;

    axios
      .put(
        `http://localhost:8080/contracts/${contractId}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .catch((err) => console.error(err));
  };

  // Add milestone
  const handleAddMilestone = (e) => {
    e.preventDefault();
    axios
      .post(
        "http://localhost:8080/milestones",
        {
          projectId,
          description,
          status,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => {
        setMilestones([...milestones, res.data]);
        setDescription("");
        setStatus("PENDING");
        setShowForm(false);
      })
      .catch((err) => console.error(err));
  };

  // Change milestone status
  const handleStatusChange = (id, newStatus) => {
    axios
      .put(
        `http://localhost:8080/milestones/${id}`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then(() => {
        setMilestones((prev) =>
          prev.map((m) => (m.id === id ? { ...m, status: newStatus } : m))
        );
      })
      .catch((err) => console.error(err));
  };

  // Delete file
  const handleDeleteFile = (milestoneId) => {
    axios
      .delete(`http://localhost:8080/milestones/${milestoneId}/file`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        fetchMilestones();
      })
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Milestones for Project {projectId}</h2>

      {/* Editable Contract Status */}
      {contractStatus && (
        <div style={{ marginBottom: "20px" }}>
          <label style={{ fontWeight: "bold" }}>Contract Status: </label>
          <select
            value={contractStatus}
            onChange={(e) => handleContractStatusChange(e.target.value)}
            style={{
              padding: "5px",
              borderRadius: "4px",
              backgroundColor:
                contractStatus === "ONGOING"
                  ? "#e0f7e9"
                  : contractStatus === "COMPLETED"
                  ? "#e6f0ff"
                  : "#fff3cd",
            }}
          >
            <option value="PENDING">Pending</option>
            <option value="COMPLETED">Completed</option>
          </select>
        </div>
      )}

      {/* Add milestone button */}
      <button
        onClick={() => setShowForm(!showForm)}
        style={{
          marginBottom: "15px",
          padding: "5px 10px",
          backgroundColor: "#007bff",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        {showForm ? "Cancel" : "Add Milestone"}
      </button>

      {/* Add milestone form */}
      {showForm && (
        <form
          onSubmit={handleAddMilestone}
          style={{
            marginBottom: "20px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
            background: "#f9f9f9",
          }}
        >
          <div style={{ marginBottom: "10px" }}>
            <label>Description:</label>
            <br />
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              style={{ width: "100%", height: "60px" }}
            />
          </div>
          <div style={{ marginBottom: "10px" }}>
            <label>Status:</label>
            <br />
            <select
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              style={{ width: "100%" }}
            >
              <option value="PENDING">Pending</option>
              <option value="COMPLETED">Completed</option>
            </select>
          </div>
          <button
            type="submit"
            style={{
              padding: "5px 10px",
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
            }}
          >
            Save Milestone
          </button>
        </form>
      )}

      {/* Milestones list */}
      {milestones.length === 0 ? (
        <p>No milestones yet.</p>
      ) : (
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
            gap: "15px",
          }}
        >
          {milestones.map((m) => (
            <div
              key={m.id}
              style={{
                padding: "15px",
                border: "1px solid #ddd",
                borderRadius: "8px",
                background: "white",
                boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
              }}
            >
              <p style={{ margin: "0 0 10px" }}>{m.description}</p>
              <select
                value={m.status}
                onChange={(e) => handleStatusChange(m.id, e.target.value)}
                style={{
                  padding: "4px 8px",
                  borderRadius: "4px",
                  marginBottom: "10px",
                  width: "100%",
                }}
              >
                <option value="PENDING">Pending</option>
                <option value="COMPLETED">Completed</option>
              </select>

              {/* File actions */}
              <button
                style={{
                  marginTop: "10px",
                  padding: "5px 10px",
                  backgroundColor: "#17a2b8",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  width: "100%",
                }}
                onClick={() =>
                  navigate(`/projects/${projectId}/milestones/${m.id}/upload`)
                }
              >
                Upload File
              </button>

              {m.fileName && (
                <button
                  style={{
                    marginTop: "8px",
                    padding: "5px 10px",
                    backgroundColor: "#dc3545",
                    color: "white",
                    border: "none",
                    borderRadius: "4px",
                    cursor: "pointer",
                    width: "100%",
                  }}
                  onClick={() => handleDeleteFile(m.id)}
                >
                  Delete File
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
