import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function Proposal() {
  const { projectId } = useParams();
  const [proposals, setProposals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchProposals = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/proposal/client/${localStorage.getItem("userId")}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        // Filter proposals for this project
        const filtered = response.data.filter(p => p.projectId === parseInt(projectId));
        setProposals(filtered);
      } catch (err) {
        console.error(err.response || err);
        setError("Error fetching proposals.");
      } finally {
        setLoading(false);
      }
    };

    fetchProposals();
  }, [projectId, token]);

  const updateProposalStatus = async (proposalId, status) => {
    try {
      await axios.put(
        `http://localhost:8080/proposal/${proposalId}`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setProposals(prev =>
        prev.map(p => (p.id === proposalId ? { ...p, status } : p))
      );
    } catch (err) {
      console.error(err.response || err);
      alert("Failed to update status");
    }
  };

  if (loading) return <p>Loading proposals...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Proposals for Project #{projectId}</h2>
      {proposals.length === 0 ? (
        <p>No proposals submitted yet.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {proposals.map((proposal) => (
            <li
              key={proposal.id}
              style={{
                marginBottom: "15px",
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "5px",
              }}
            >
              <p><strong>Freelancer ID:</strong> {proposal.freelancerId}</p>
              <p><strong>Bid Amount:</strong> {proposal.bidAmount}</p>
              <p><strong>Status:</strong> {proposal.status}</p>
              <button
                onClick={() => updateProposalStatus(proposal.id, "ACCEPTED")}
                disabled={proposal.status !== "PENDING"}
                style={{ marginRight: "10px" }}
              >
                Accept
              </button>
              <button
                onClick={() => updateProposalStatus(proposal.id, "REJECTED")}
                disabled={proposal.status !== "PENDING"}
              >
                Reject
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
