import React, { useEffect, useState } from "react";
import axios from "axios";

export default function ManageUsers() {
  const [clients, setClients] = useState([]);
  const [freelancers, setFreelancers] = useState([]);
  const [projects, setProjects] = useState([]);
  const [contracts, setContracts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch all users
        const userRes = await axios.get("http://localhost:8080/api/auth/users");
        const allUsers = userRes.data;
        setClients(allUsers.filter(user => user.role.toUpperCase() === "CLIENT"));
        setFreelancers(allUsers.filter(user => user.role.toUpperCase() === "FREELANCER"));

        // Fetch all projects
        const projectRes = await axios.get("http://localhost:8080/projects");
        setProjects(projectRes.data);

        // Fetch all contracts
        const contractRes = await axios.get("http://localhost:8080/contracts");
        setContracts(contractRes.data);

      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Clients ({clients.length})</h1>
      {clients.length === 0 ? <p>No clients found.</p> :
        <ul>
          {clients.map(client => (
            <li key={client.id}>{client.name} ({client.email})</li>
          ))}
        </ul>
      }

      <h1>Freelancers ({freelancers.length})</h1>
      {freelancers.length === 0 ? <p>No freelancers found.</p> :
        <ul>
          {freelancers.map(freelancer => (
            <li key={freelancer.id}>{freelancer.name} ({freelancer.email})</li>
          ))}
        </ul>
      }

      <h1>Projects ({projects.length})</h1>
      {projects.length === 0 ? <p>No projects found.</p> :
        <ul>
          {projects.map(project => (
            <li key={project.id}>
              <strong>{project.title}</strong> - Client ID: {project.clientId} - Status: {project.status}
            </li>
          ))}
        </ul>
      }

      <h1>Contracts ({contracts.length})</h1>
      {contracts.length === 0 ? <p>No contracts found.</p> :
        <ul>
          {contracts.map(contract => (
            <li key={contract.id}>
              Contract ID: {contract.id} - Project ID: {contract.projectId} - Client ID: {contract.clientId} - Freelancer ID: {contract.freelancerId} - Status: {contract.status}
            </li>
          ))}
        </ul>
      }
    </div>
  );
}
