import React, { useState } from "react";
import axios from "axios";

export default function PostProject() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    minBudget: "",
    maxBudget: "",
    deadline: "",
    skills: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem("token");
    const clientId = Number(localStorage.getItem("userId"));

    if (!token) return setMessage("You are not logged in!");
    if (!clientId) return setMessage("User ID not found!");

    const projectData = {
      ...formData,
      clientId,
      minBudget: Number(formData.minBudget),
      maxBudget: Number(formData.maxBudget),
      skills: formData.skills.split(",").map((s) => s.trim()),
      deadline: new Date(formData.deadline).toISOString(),
    };

    console.log("Sending project:", projectData);

    try {
      await axios.post("http://localhost:8080/projects", projectData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage("Project posted successfully!");
      setFormData({ title: "", description: "", minBudget: "", maxBudget: "", deadline: "", skills: "" });
    } catch (error) {
      console.error(error.response?.data || error.message);
      setMessage("Error posting project. Check console for details.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Post a New Project</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", maxWidth: "400px" }}>
        <input type="text" name="title" placeholder="Project Title" value={formData.title} onChange={handleChange} required />
        <textarea name="description" placeholder="Project Description" value={formData.description} onChange={handleChange} required />
        <input type="number" name="minBudget" placeholder="Minimum Budget" value={formData.minBudget} onChange={handleChange} required />
        <input type="number" name="maxBudget" placeholder="Maximum Budget" value={formData.maxBudget} onChange={handleChange} required />
        <input type="date" name="deadline" value={formData.deadline} onChange={handleChange} required />
        <input type="text" name="skills" placeholder="Skills (comma separated)" value={formData.skills} onChange={handleChange} required />
        <button type="submit" style={{ marginTop: "10px", padding: "8px", background: "#222", color: "#fff" }}>
          Post Project
        </button>
      </form>
    </div>
  );
}
