import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../api";
import { validateEmail, validatePassword } from "../utils";

export default function Login({ setToken, setRole }) {
    const navigate = useNavigate();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleLogin = async (e) => {
        e.preventDefault();
        setError("");

        if (!validateEmail(email) || !validatePassword(password)) {
            setError("Please enter a valid email and password (min 6 characters).");
            return;
        }

        setLoading(true);
        try {
            const userData = await loginUser({ email, password });

            if (userData.token) {
                localStorage.setItem("token", userData.token);
                setToken(userData.token);
            }
            if (userData.role) {
                localStorage.setItem("role", userData.role.toUpperCase());
                setRole(userData.role.toUpperCase());
            }
            if (userData.id) localStorage.setItem("userId", userData.id);

            const roleUpper = (userData.role || "").toUpperCase();
            if (roleUpper === "ADMIN") navigate("/manage-users", { replace: true });
            else if (roleUpper === "CLIENT") navigate("/dashboard", { replace: true });
            else if (roleUpper === "FREELANCER") navigate("/freelancer-dashboard", { replace: true });
            else navigate("/login", { replace: true });

        } catch (err) {
            console.error("Login failed:", err);
            setError("Invalid credentials. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.container}>
            {/* Left Section - Branding */}
            <div style={styles.leftPanel}>
                <h1 style={styles.brandTitle}>FreelanceHub</h1>
                <p style={styles.tagline}>
                    Connect with top freelancers and clients worldwide. 
                </p>
                <ul style={styles.features}>
                    <li>✔ Hire professionals for any project</li>
                    <li>✔ Work remotely with global talent</li>
                    <li>✔ Secure payments and trusted reviews</li>
                </ul>
                <img
                    src="https://cdn-icons-png.flaticon.com/512/4076/4076549.png"
                    alt="freelancer illustration"
                    style={styles.illustration}
                />
            </div>

            {/* Right Section - Login Form */}
            <div style={styles.rightPanel}>
                <form style={styles.form} onSubmit={handleLogin}>
                    <h2 style={styles.title}>Welcome Back</h2>
                    <p style={styles.subtitle}>Sign in to access your dashboard</p>
                    {error && <p style={styles.error}>{error}</p>}
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        style={styles.input}
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        style={styles.input}
                    />
                    <button type="submit" style={styles.button} disabled={loading}>
                        {loading ? "Logging in..." : "Login"}
                    </button>
                    <p style={styles.registerText}>
                        No account?{" "}
                        <span
                            style={styles.registerLink}
                            onClick={() => navigate("/register")}
                        >
                            Register here
                        </span>
                    </p>
                </form>
            </div>
        </div>
    );
}

const styles = {
    container: {
        display: "flex",
        height: "100vh",
        fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    },
    leftPanel: {
        flex: 1,
        background: "linear-gradient(135deg, #007BFF, #00C6FF)",
        color: "#fff",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "flex-start",
        padding: "60px",
    },
    brandTitle: {
        fontSize: "36px",
        fontWeight: "700",
        marginBottom: "15px",
    },
    tagline: {
        fontSize: "18px",
        marginBottom: "20px",
        lineHeight: "1.4",
        maxWidth: "350px",
    },
    features: {
        listStyle: "none",
        padding: 0,
        marginBottom: "30px",
        fontSize: "16px",
        lineHeight: "1.8",
    },
    illustration: {
        width: "280px",
        marginTop: "20px",
    },
    rightPanel: {
        flex: 1,
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "#f9fafc",
    },
    form: {
        background: "#fff",
        padding: "40px 35px",
        borderRadius: "12px",
        boxShadow: "0 10px 25px rgba(0,0,0,0.1)",
        width: "360px",
        textAlign: "center",
    },
    title: {
        marginBottom: "8px",
        color: "#333",
        fontSize: "26px",
        fontWeight: "600",
    },
    subtitle: {
        marginBottom: "20px",
        color: "#555",
        fontSize: "14px",
    },
    input: {
        width: "100%",
        padding: "12px",
        margin: "10px 0",
        border: "1px solid #ccc",
        borderRadius: "8px",
        fontSize: "14px",
        outline: "none",
    },
    button: {
        width: "100%",
        padding: "12px",
        marginTop: "15px",
        backgroundColor: "#007BFF",
        color: "#fff",
        border: "none",
        borderRadius: "8px",
        cursor: "pointer",
        fontSize: "16px",
        fontWeight: "500",
    },
    error: { color: "red", fontSize: "14px", marginBottom: "10px" },
    registerText: { marginTop: "15px", fontSize: "14px", color: "#555" },
    registerLink: { color: "#007BFF", cursor: "pointer", textDecoration: "underline" },
};
