import React, { useState } from "react";
import axios from "axios";

export default function PostProject() {
  const [formData, setFormData] = useState({
    title: "",
    description: "",
    minBudget: "",
    maxBudget: "",
    deadline: "",
    skills: "",
  });

  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Convert skills comma-separated string to array
    const projectData = {
      ...formData,
      minBudget: Number(formData.minBudget),
      maxBudget: Number(formData.maxBudget),
      clientId: Number(localStorage.getItem("userId")), // assuming you store client id
      skills: formData.skills.split(",").map((s) => s.trim()),
    };

    try {
      const token = localStorage.getItem("token");
      const response = await axios.post("http://localhost:8080/projects", projectData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessage("Project posted successfully!");
      setFormData({
        title: "",
        description: "",
        minBudget: "",
        maxBudget: "",
        deadline: "",
        skills: "",
      });
    } catch (error) {
      console.error(error);
      setMessage("Error posting project. Please try again.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Post a New Project</h2>
      {message && <p>{message}</p>}
      <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", maxWidth: "400px" }}>
        <input
          type="text"
          name="title"
          placeholder="Project Title"
          value={formData.title}
          onChange={handleChange}
          required
        />
        <textarea
          name="description"
          placeholder="Project Description"
          value={formData.description}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="minBudget"
          placeholder="Minimum Budget"
          value={formData.minBudget}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="maxBudget"
          placeholder="Maximum Budget"
          value={formData.maxBudget}
          onChange={handleChange}
          required
        />
        <input
          type="date"
          name="deadline"
          value={formData.deadline}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="skills"
          placeholder="Skills (comma separated)"
          value={formData.skills}
          onChange={handleChange}
          required
        />
        <button type="submit" style={{ marginTop: "10px", padding: "8px", background: "#222", color: "#fff" }}>
          Post Project
        </button>
      </form>
    </div>
  );
}
