import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import PostProject from "./pages/PostProject";
import BrowseProjects from "./pages/BrowseProjects";
import ManageUsers from "./pages/ManageUsers";
import FreelancerDashboard from "./pages/FreelancerDashboard";
import Proposal from "./pages/Proposal";


function App() {
  const [token, setToken] = useState(localStorage.getItem("token"));
  const [role, setRole] = useState((localStorage.getItem("role") || "").toUpperCase());

  // Update state if localStorage changes (optional)
  useEffect(() => {
    const handleStorageChange = () => {
      setToken(localStorage.getItem("token"));
      setRole((localStorage.getItem("role") || "").toUpperCase());
    };
    window.addEventListener("storage", handleStorageChange);
    return () => window.removeEventListener("storage", handleStorageChange);
  }, []);

  const getDashboardRoute = () => {
    switch (role) {
      case "ADMIN":
        return "/manage-users";
      case "CLIENT":
        return "/dashboard";
      case "FREELANCER":
        return "/freelancer-dashboard";
      default:
        return "/login";
    }
  };

  return (
    <Router>
      {token && <Navbar />}
      <Routes>
        {/* Redirect root and login if already logged in */}
        <Route path="/" element={token ? <Navigate to={getDashboardRoute()} /> : <Login setToken={setToken} setRole={setRole} />} />
        <Route path="/login" element={token ? <Navigate to={getDashboardRoute()} /> : <Login setToken={setToken} setRole={setRole} />} />
        <Route path="/register" element={token ? <Navigate to={getDashboardRoute()} /> : <Register />} />

        {/* Protected routes */}
        <Route path="/dashboard" element={token && role === "CLIENT" ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/post-project" element={token && role === "CLIENT" ? <PostProject /> : <Navigate to="/login" />} />
        <Route path="/browse-projects" element={token && role === "FREELANCER" ? <BrowseProjects /> : <Navigate to="/login" />} />
        <Route path="/freelancer-dashboard" element={token && role === "FREELANCER" ? <FreelancerDashboard /> : <Navigate to="/login" />} />
        <Route path="/manage-users" element={token && role === "ADMIN" ? <ManageUsers /> : <Navigate to="/login" />} />
        <Route path="/proposals/:projectId" element={token && role === "CLIENT" ? <Proposal /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
