import React, { useEffect, useState } from "react";
import axios from "axios";

export default function ManageUsers() {
  const [clients, setClients] = useState([]);
  const [projects, setProjects] = useState({}); // { clientId: [projects] }
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchClientsAndProjects = async () => {
      try {
        // 1️⃣ Fetch all users
        const res = await axios.get("http://localhost:8080/api/auth/users");
        const allClients = res.data.filter(user => user.role.toUpperCase() === "CLIENT");
        setClients(allClients);

        // 2️⃣ Fetch all projects in parallel
        const projectPromises = allClients.map(client =>
          axios.get(`http://localhost:8080/projects/client/${client.id}`)
            .then(projRes => ({ clientId: client.id, projects: projRes.data }))
        );

        const projectResults = await Promise.all(projectPromises);

        // 3️⃣ Build project map
        const projectMap = {};
        projectResults.forEach(item => {
          projectMap[item.clientId] = item.projects;
        });
        setProjects(projectMap);

      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchClientsAndProjects();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Clients and Their Projects</h1>
      {clients.length === 0 && <p>No clients found.</p>}
      {clients.map(client => (
        <div key={client.id} style={{ marginBottom: "30px", borderBottom: "1px solid #ccc", paddingBottom: "15px" }}>
          <h2>{client.name} ({client.email})</h2>
          {projects[client.id] && projects[client.id].length > 0 ? (
            <ul>
              {projects[client.id].map(project => (
                <li key={project.id}>
                  <strong>{project.title}</strong> - Status: {project.status} - Budget: ${project.minBudget} - ${project.maxBudget}
                </li>
              ))}
            </ul>
          ) : (
            <p>No projects for this client.</p>
          )}
        </div>
      ))}
    </div>
  );
}
