import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { registerUser } from "../api"; // backend API call
import { validateEmail, validatePassword } from "../utils"; // helper functions

export default function Register() {
    const navigate = useNavigate();
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleRegister = async (e) => {
        e.preventDefault();
        setError("");

        // Simple frontend validation
        if (!name.trim()) {
            setError("Name is required.");
            return;
        }
        if (!validateEmail(email)) {
            setError("Invalid email format.");
            return;
        }
        if (!validatePassword(password)) {
            setError("Password must be at least 6 characters.");
            return;
        }

        setLoading(true);
        try {
            const newUser = await registerUser({ name, email, password }); // API call
            console.log("Registration successful:", newUser);

            // Optional: redirect to login after successful register
            navigate("/login");
        } catch (err) {
            console.error("Registration failed:", err);
            setError("Registration failed. Email may already be in use.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.container}>
            <form style={styles.form} onSubmit={handleRegister}>
                <h2>Register</h2>

                {error && <p style={styles.error}>{error}</p>}

                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    style={styles.input}
                />

                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    style={styles.input}
                />

                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={styles.input}
                />

                <button type="submit" style={styles.button} disabled={loading}>
                    {loading ? "Registering..." : "Register"}
                </button>

                <p style={styles.registerText}>
                    Already have an account?{" "}
                    <span
                        style={styles.registerLink}
                        onClick={() => navigate("/login")}
                    >
                        Login here
                    </span>
                </p>
            </form>
        </div>
    );
}

const styles = {
    container: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        background: "#f4f4f4",
    },
    form: {
        background: "#fff",
        padding: "30px",
        borderRadius: "8px",
        boxShadow: "0px 4px 12px rgba(0,0,0,0.1)",
        width: "300px",
    },
    input: {
        width: "100%",
        padding: "10px",
        margin: "8px 0",
        border: "1px solid #ccc",
        borderRadius: "4px",
    },
    button: {
        width: "100%",
        padding: "10px",
        background: "#28a745",
        color: "#fff",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
    },
    error: {
        color: "red",
        fontSize: "14px",
    },
    registerText: {
        marginTop: "10px",
        fontSize: "14px",
        textAlign: "center",
    },
    registerLink: {
        color: "#007BFF",
        cursor: "pointer",
        textDecoration: "underline",
    },
};
