import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function FileUpload() {
  const { projectId, milestoneId } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState("");
  const [uploadedFiles, setUploadedFiles] = useState([]);

  // Fetch uploaded files on mount
  useEffect(() => {
    fetchUploadedFiles();
  }, [projectId]);

  const fetchUploadedFiles = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8080/files/project/${projectId}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setUploadedFiles(res.data || []);
    } catch (err) {
      console.error("Error fetching files:", err);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setMessage("");
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      setMessage("‚ö† Please choose a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("projectId", projectId);

    setUploading(true);
    setMessage("");

    try {
      const res = await axios.post(
        "http://localhost:8080/files/upload",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(`‚úÖ File "${res.data.fileName}" uploaded successfully!`);
      setFile(null);

      // Refresh file list
      fetchUploadedFiles();
    } catch (err) {
      console.error("Upload failed:", err);
      setMessage(
        err.response?.data?.message ||
          "‚ùå Failed to upload file. Please try again."
      );
    } finally {
      setUploading(false);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Upload File for Project {projectId}</h2>

      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        {file && <p style={{ marginTop: "5px" }}>üìÑ Selected: {file.name}</p>}

        <button
          type="submit"
          disabled={uploading}
          style={{
            marginTop: "10px",
            padding: "5px 10px",
            backgroundColor: uploading ? "#6c757d" : "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: uploading ? "not-allowed" : "pointer",
          }}
        >
          {uploading ? "Uploading..." : "Upload"}
        </button>
      </form>

      {message && <p style={{ marginTop: "10px" }}>{message}</p>}

      <hr style={{ margin: "20px 0" }} />

      <h3>Uploaded Files</h3>
      {uploadedFiles.length > 0 ? (
        <ul>
          {uploadedFiles.map((f, index) => (
            <li key={index}>
              <a
                href={`http://localhost:8080/files/download/${f.id}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                {f.fileName}
              </a>
            </li>
          ))}
        </ul>
      ) : (
        <p>No files uploaded yet.</p>
      )}
    </div>
  );
}
