import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Dashboard() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const clientId = localStorage.getItem("userId");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!clientId || !token) {
      setError("Client not logged in!");
      setLoading(false);
      return;
    }

    const fetchProjects = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/projects/client/${clientId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        // For each project, fetch proposals to check if any are ACCEPTED
        const enriched = await Promise.all(
          response.data.map(async (project) => {
            const proposalsRes = await axios.get(
              `http://localhost:8080/proposal/project/${project.id}`,
              { headers: { Authorization: `Bearer ${token}` } }
            );

            // Check if there's an accepted proposal
            const acceptedProposal = proposalsRes.data.find(
              (proposal) => proposal.status === "ACCEPTED"
            );

            return { ...project, acceptedProposal };
          })
        );

        setProjects(enriched);
      } catch (err) {
        console.error(err.response || err);
        setError("Error fetching projects.");
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, [clientId, token]);

  const viewProposals = (projectId) => {
    navigate(`/proposals/${projectId}`);
  };

  const viewMilestones = (projectId) => {
    navigate(`/milestones/${projectId}`);
  };

  if (loading) return <p>Loading projects...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Your Posted Projects</h2>
      {projects.length === 0 ? (
        <p>No projects posted yet.</p>
      ) : (
        <ul>
          {projects.map((project) => (
            <li
              key={project.id}
              style={{
                marginBottom: "15px",
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "5px",
              }}
            >
              <h3>{project.title}</h3>
              <p>
                <strong>Description:</strong> {project.description}
              </p>
              <p>
                <strong>Budget:</strong> {project.minBudget} - {project.maxBudget}
              </p>
              <p>
                <strong>Deadline:</strong>{" "}
                {new Date(project.deadline).toLocaleDateString()}
              </p>
              <p>
                <strong>Skills:</strong> {project.skills.join(", ")}
              </p>
              <p>
                <strong>Status:</strong> {project.status}
              </p>

              <button
                onClick={() => viewProposals(project.id)}
                style={{
                  marginTop: "10px",
                  padding: "5px 10px",
                  marginRight: "10px",
                }}
              >
                View Proposals
              </button>

              {project.acceptedProposal && (
                <button
                  style={{
                    padding: "6px 12px",
                    backgroundColor: "#007bff",
                    color: "white",
                    border: "none",
                    borderRadius: "4px",
                    cursor: "pointer",
                  }}
                  onClick={() => viewMilestones(project.id)}
                >
                  View More
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
