import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Profile() {
  const [user, setUser] = useState(null);
  const [projectsCount, setProjectsCount] = useState(0);
  const [proposalsCount, setProposalsCount] = useState(0);
  const userId = parseInt(localStorage.getItem("userId")); // make sure it's a number
  const role = (localStorage.getItem("role") || "").toUpperCase();

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch all users and find current user
        const resUsers = await axios.get("http://localhost:8080/api/auth/users");
        const currentUser = resUsers.data.find(u => u.id === userId);
        setUser(currentUser);

        if (role === "CLIENT") {
          // Client: fetch projects posted
          const resProjects = await axios.get(`http://localhost:8080/projects/client/${userId}`);
          setProjectsCount(resProjects.data.length);

          // Optional: fetch proposals if you have endpoint
          const resProposals = await axios.get(`http://localhost:8080/proposals/client/${userId}`);
          setProposalsCount(resProposals.data.length);

        } else if (role === "FREELANCER") {
          // Freelancer: fetch contracts assigned
          const resContracts = await axios.get(`http://localhost:8080/contracts/freelancer/${userId}`);
          setProjectsCount(resContracts.data.length);

          // Optional: fetch proposals if you have endpoint
          const resProposals = await axios.get(`http://localhost:8080/proposals/freelancer/${userId}`);
          setProposalsCount(resProposals.data.length);
        }

      } catch (err) {
        console.error("Error fetching profile data:", err);
      }
    };

    fetchData();
  }, [userId, role]);

  if (!user) return <p>Loading profile...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Profile</h1>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Role:</strong> {user.role}</p>
      <p><strong>Total Projects:</strong> {projectsCount}</p>
      <p><strong>Total Proposals:</strong> {proposalsCount}</p>
    </div>
  );
}
