import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { loginUser } from "../api"; // backend API call
import { validateEmail, validatePassword } from "../utils"; // helper functions
export default function Login({ setToken, setRole }) {
    const navigate = useNavigate();
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleLogin = async (e) => {
        e.preventDefault();
        setError("");

        if (!validateEmail(email) || !validatePassword(password)) {
            setError("Please enter a valid email and password (min 6 characters).");
            return;
        }

        setLoading(true);
        try {
            const userData = await loginUser({ email, password });

            if (userData.token) {
                localStorage.setItem("token", userData.token);
                setToken(userData.token); // update App.js state
            }
            if (userData.role) {
                localStorage.setItem("role", userData.role.toUpperCase());
                setRole(userData.role.toUpperCase()); // update App.js state
            }
            if (userData.id) localStorage.setItem("userId", userData.id);

            // Navigate based on role
            const roleUpper = (userData.role || "").toUpperCase();
            if (roleUpper === "ADMIN") navigate("/manage-users", { replace: true });
            else if (roleUpper === "CLIENT") navigate("/dashboard", { replace: true });
            else if (roleUpper === "FREELANCER") navigate("/freelancer-dashboard", { replace: true });
            else navigate("/login", { replace: true });

        } catch (err) {
            console.error("Login failed:", err);
            setError("Invalid credentials. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    // ... rest of the Login.js form remains the same



    return (
        <div style={styles.container}>
            <form style={styles.form} onSubmit={handleLogin}>
                <h2>Login</h2>
                {error && <p style={styles.error}>{error}</p>}
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    style={styles.input}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    style={styles.input}
                />
                <button type="submit" style={styles.button} disabled={loading}>
                    {loading ? "Logging in..." : "Login"}
                </button>
                <p style={styles.registerText}>
                    No account?{" "}
                    <span
                        style={styles.registerLink}
                        onClick={() => navigate("/register")}
                    >
                        Register here
                    </span>
                </p>
            </form>
        </div>
    );
}

const styles = {
    container: { display: "flex", justifyContent: "center", alignItems: "center", height: "100vh", background: "#f4f4f4" },
    form: { background: "#fff", padding: "30px", borderRadius: "8px", boxShadow: "0px 4px 12px rgba(0,0,0,0.1)", width: "300px" },
    input: { width: "100%", padding: "10px", margin: "8px 0", border: "1px solid #ccc", borderRadius: "4px" },
    button: { width: "100%", padding: "10px", background: "#007BFF", color: "#fff", border: "none", borderRadius: "4px", cursor: "pointer" },
    error: { color: "red", fontSize: "14px" },
    registerText: { marginTop: "10px", fontSize: "14px", textAlign: "center" },
    registerLink: { color: "#007BFF", cursor: "pointer", textDecoration: "underline" },
};
