import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function VisitProfile() {
  const { userId } = useParams();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/api/auth/users/${userId}`);
        setUser(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchUser();
  }, [userId]);

  if (!user) return <p style={{ textAlign: "center", marginTop: "50px" }}>Loading profile...</p>;

  return (
    <div style={{ maxWidth: "500px", margin: "50px auto", padding: "30px", borderRadius: "12px", background: "#f8f9fa", boxShadow: "0 4px 20px rgba(0,0,0,0.1)", textAlign: "center" }}>
      <h1 style={{ marginBottom: "20px", fontSize: "2rem", color: "#333", borderBottom: "2px solid #eee", paddingBottom: "10px" }}>Profile</h1>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Role:</strong> {user.role}</p>
    </div>
  );
}
