import React, { useEffect, useState } from "react";
import axios from "axios";

export default function FreelancerDashboard() {
  const [proposals, setProposals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");
  const freelancerId = localStorage.getItem("userId");

  useEffect(() => {
    if (!token) {
      setError("You are not logged in!");
      setLoading(false);
      return;
    }

    const fetchProposals = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/proposals/freelancer/${freelancerId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        const enriched = await Promise.all(response.data.map(async (proposal) => {
          const projectRes = await axios.get(
            `http://localhost:8080/projects/${proposal.projectId}`,
            { headers: { Authorization: `Bearer ${token}` } }
          );
          return { ...proposal, project: projectRes.data };
        }));

        // Sort by status: ACCEPTED -> REJECTED -> PENDING
        const sorted = enriched.sort((a, b) => {
          const order = { ACCEPTED: 0, REJECTED: 1, PENDING: 2 };
          return order[a.status] - order[b.status];
        });

        setProposals(sorted);
      } catch (err) {
        console.error(err.response || err);
        setError("Error fetching proposals.");
      } finally {
        setLoading(false);
      }
    };

    fetchProposals();
  }, [token, freelancerId]);

  if (loading) return <p>Loading proposals...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>My Projects</h2>
      {proposals.length === 0 ? (
        <p>No proposals submitted yet.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {proposals.map((proposal) => (
            <li
              key={proposal.id}
              style={{
                marginBottom: "15px",
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "5px",
              }}
            >
              <h3>{proposal.project.title}</h3>
              <p><strong>Description:</strong> {proposal.project.description}</p>
              <p><strong>Budget:</strong> {proposal.project.minBudget} - {proposal.project.maxBudget}</p>
              <p><strong>Deadline:</strong> {new Date(proposal.project.deadline).toLocaleDateString()}</p>
              <p><strong>Skills:</strong> {proposal.project.skills.join(", ")}</p>
              <p><strong>Bid Amount:</strong> {proposal.bidAmount}</p>
              <p><strong>Status:</strong> {proposal.status}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
