import React, { useEffect, useState } from "react";
import axios from "axios";

export default function MyProjects() {
  const [projects, setProjects] = useState([]);
  const [message, setMessage] = useState("");
  const token = localStorage.getItem("token");
  const clientId = Number(localStorage.getItem("userId"));

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const res = await axios.get(`http://localhost:8080/projects/client/${clientId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProjects(res.data);
      } catch (err) {
        console.error(err.response || err);
        setMessage("Error fetching projects.");
      }
    };
    fetchProjects();
  }, [clientId, token]);

  const deleteProject = async (projectId) => {
    if (!window.confirm("Are you sure you want to delete this project?")) return;

    try {
      await axios.delete(`http://localhost:8080/projects/delete/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProjects(projects.filter(p => p.id !== projectId));
      setMessage("Project deleted successfully!");
    } catch (err) {
      console.error(err.response || err);
      setMessage("Failed to delete project.");
    }
  };

  return (
    <div style={{ padding: "40px", maxWidth: "700px", margin: "50px auto" }}>
      <h2>My Projects</h2>
      {message && <p style={{ color: "red" }}>{message}</p>}
      {projects.length === 0 ? (
        <p>No projects found.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {projects.map(project => (
            <li
              key={project.id}
              style={{
                border: "1px solid #ccc",
                borderRadius: "6px",
                padding: "15px",
                marginBottom: "15px",
                position: "relative"
              }}
            >
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <p><strong>Budget:</strong> {project.minBudget} - {project.maxBudget}</p>
              <p><strong>Deadline:</strong> {new Date(project.deadline).toLocaleDateString()}</p>
              <p><strong>Skills:</strong> {project.skills.join(", ")}</p>
              <button
                onClick={() => deleteProject(project.id)}
                style={{
                  position: "absolute",
                  top: "15px",
                  right: "15px",
                  background: "#f44336",
                  color: "#fff",
                  border: "none",
                  borderRadius: "5px",
                  padding: "6px 12px",
                  cursor: "pointer"
                }}
                onMouseOver={(e) => e.currentTarget.style.background = "#d32f2f"}
                onMouseOut={(e) => e.currentTarget.style.background = "#f44336"}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
