// src/components/Header.js
import React from "react";

export default function Header() {
  const headerStyle = {
    background: "linear-gradient(90deg, #6a11cb 0%, #2575fc 100%)", // gradient
    color: "#fff",
    padding: "25px 20px",
    textAlign: "center",
    fontSize: "26px",
    fontWeight: "700",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.3)",
    borderBottomLeftRadius: "12px",
    borderBottomRightRadius: "12px",
    position: "relative",
    overflow: "hidden",
    animation: "slideDown 0.8s ease-out",
  };

  const subTextStyle = {
    fontSize: "16px",
    fontWeight: "400",
    marginTop: "8px",
    fontStyle: "italic",
    color: "#f0f0f0",
    opacity: 0,
    animation: "fadeIn 1s 0.5s forwards",
  };

  const taglineStyle = {
    fontSize: "14px",
    marginTop: "6px",
    color: "#d1d5db",
    fontStyle: "normal",
    opacity: 0,
    animation: "fadeIn 1s 1s forwards",
  };

  return (
    <header style={headerStyle}>
      My Freelance Platform
      <div style={subTextStyle}>Where projects meet talent!</div>
      <div style={taglineStyle}>
        Discover opportunities, collaborate seamlessly, and grow your career.
      </div>

      {/* Keyframes for animations */}
      <style>
        {`
          @keyframes slideDown {
            0% { transform: translateY(-50px); opacity: 0; }
            100% { transform: translateY(0); opacity: 1; }
          }
          @keyframes fadeIn {
            to { opacity: 1; }
          }
        `}
      </style>
    </header>
  );
}
