import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Contracts() {
  const [contracts, setContracts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");
  const role = (localStorage.getItem("role") || "").toUpperCase();
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    const fetchContracts = async () => {
      try {
        let url = "";
        if (role === "CLIENT") url = `http://localhost:8080/contracts/client/${userId}`;
        if (role === "FREELANCER") url = `http://localhost:8080/contracts/freelancer/${userId}`;

        const response = await axios.get(url, {
          headers: { Authorization: `Bearer ${token}` }
        });

        setContracts(response.data);
      } catch (err) {
        console.error(err.response || err);
        setError("Failed to fetch contracts");
      } finally {
        setLoading(false);
      }
    };

    fetchContracts();
  }, [role, userId, token]);

  if (loading) return <p>Loading contracts...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Contracts</h2>
      {contracts.length === 0 ? (
        <p>No approved projects/contracts yet.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {contracts.map(contract => (
            <li
              key={contract.id}
              style={{
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "5px",
                marginBottom: "15px"
              }}
            >
              <p><strong>Project ID:</strong> {contract.proposalId}</p>
              <p><strong>Contract Description:</strong> {contract.description}</p>
              <p><strong>Status:</strong> {contract.status}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
