import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Dashboard() {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const clientId = localStorage.getItem("userId");
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!clientId || !token) {
      setError("Client not logged in!");
      setLoading(false);
      return;
    }

    const fetchProjects = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/projects/client/${clientId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        // Check contracts for each project
        const projectsWithContracts = await Promise.all(
          response.data.map(async (project) => {
            try {
              const res = await axios.get(
                `http://localhost:8080/contracts/project/${project.id}`,
                { headers: { Authorization: `Bearer ${token}` } }
              );
              return { ...project, hasContract: res.data.length > 0 };
            } catch {
              return { ...project, hasContract: false };
            }
          })
        );

        setProjects(projectsWithContracts);
      } catch (err) {
        console.error(err.response || err);
        setError("Error fetching projects.");
      } finally {
        setLoading(false);
      }
    };

    fetchProjects();
  }, [clientId, token]);

  const viewProposals = (projectId) => {
    navigate(`/proposals/${projectId}`);
  };

  const viewMoreDetails = (projectId) => {
    navigate(`/milestones/${projectId}`);
  };

  const handleStatusChange = async (projectId, newStatus) => {
    try {
      await axios.put(
        `http://localhost:8080/projects/${projectId}/status`,
        { status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setProjects((prev) =>
        prev.map((p) =>
          p.id === projectId ? { ...p, status: newStatus } : p
        )
      );
    } catch (err) {
      console.error("Error updating status:", err);
      alert("Failed to update status.");
    }
  };

  if (loading) return <p>Loading projects...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Your Posted Projects</h2>
      {projects.length === 0 ? (
        <p>No projects posted yet.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {projects.map((project) => (
            <li
              key={project.id}
              style={{
                marginBottom: "15px",
                border: project.hasContract
                  ? "2px solid #10b981" // green border if contract exists
                  : "1px solid #ccc",
                background: project.hasContract ? "#ecfdf5" : "#fff", // subtle green background
                padding: "15px",
                borderRadius: "8px",
              }}
            >
              <h3>
                {project.title}{" "}
                {project.hasContract && (
                  <span
                    style={{
                      fontSize: "12px",
                      color: "#065f46",
                      background: "#a7f3d0",
                      padding: "2px 6px",
                      borderRadius: "4px",
                      marginLeft: "8px",
                    }}
                  >
                    Contract Exists
                  </span>
                )}
              </h3>
              <p>
                <strong>Description:</strong> {project.description}
              </p>
              <p>
                <strong>Budget:</strong> {project.minBudget} - {project.maxBudget}
              </p>
              <p>
                <strong>Deadline:</strong>{" "}
                {new Date(project.deadline).toLocaleDateString()}
              </p>
              <p>
                <strong>Skills:</strong> {project.skills.join(", ")}
              </p>

              <div>
                <strong>Status:</strong>{" "}
                <select
                  value={project.status}
                  onChange={(e) => handleStatusChange(project.id, e.target.value)}
                >
                  <option value="OPEN">OPEN</option>
                  <option value="IN_PROGRESS">IN_PROGRESS</option>
                  <option value="CLOSED">CLOSED</option>
                </select>
              </div>

              <button
                onClick={() => viewProposals(project.id)}
                style={{ marginTop: "10px", padding: "5px 10px", marginRight: "10px" }}
              >
                View Proposals
              </button>

              <button
                onClick={() => viewMoreDetails(project.id)}
                style={{ marginTop: "10px", padding: "5px 10px" }}
              >
                View More Details
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
