import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Profile() {
  const [user, setUser] = useState(null);
  const [projectsCount, setProjectsCount] = useState(0);
  const [proposalsCount, setProposalsCount] = useState(0); // if you have proposals
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    const fetchData = async () => {
      try {
        // 1️⃣ Fetch user details
        const resUser = await axios.get(`http://localhost:8080/api/auth/users/${userId}`);
        setUser(resUser.data);

        // 2️⃣ Fetch projects
        const resProjects = await axios.get(`http://localhost:8080/projects/client/${userId}`);
        setProjectsCount(resProjects.data.length);

        // 3️⃣ Fetch proposals (replace endpoint if different)
        // const resProposals = await axios.get(`http://localhost:8080/proposals/user/${userId}`);
        // setProposalsCount(resProposals.data.length);
      } catch (err) {
        console.error(err);
      }
    };

    fetchData();
  }, [userId]);

  if (!user) return <p>Loading profile...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Profile</h1>
      <p><strong>Name:</strong> {user.name}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <p><strong>Role:</strong> {user.role}</p>
      <p><strong>Total Projects:</strong> {projectsCount}</p>
      <p><strong>Total Proposals:</strong> {proposalsCount}</p>
    </div>
  );
}
