import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function Proposals() {
  const { projectId } = useParams();
  const [proposals, setProposals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchProposals = async () => {
      try {
        const response = await axios.get(
          `http://localhost:8080/proposal/client/${localStorage.getItem("userId")}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );
        // Filter for this project only
        setProposals(response.data.filter(p => p.projectId === parseInt(projectId)));
      } catch (err) {
        console.error(err.response || err);
        setError("Error fetching proposals.");
      } finally {
        setLoading(false);
      }
    };

    fetchProposals();
  }, [projectId, token]);

  const updateProposalStatus = async (proposalId, status) => {
    try {
      // Update proposal status
      await axios.put(
        `http://localhost:8080/proposals/${proposalId}`,
        { status },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // If accepted, update project status and create contract
      if (status === "ACCEPTED") {
        // Update project status
        await axios.put(
          `http://localhost:8080/projects/${projectId}/status`,
          { status: "IN_PROGRESS" },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        // Create contract for the accepted proposal
        await axios.post(
          `http://localhost:8080/contracts`,
          {
            proposalId: proposalId,
            description: "Contract for accepted proposal",
            status: "PENDING"
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }

      // Update state locally
      setProposals(prev =>
        prev.map(p => (p.id === proposalId ? { ...p, status } : p))
      );
    } catch (err) {
      console.error(err.response || err);
      alert("Failed to update proposal status");
    }
  };

  if (loading) return <p>Loading proposals...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Project Proposals</h2>
      {proposals.length === 0 ? (
        <p>No proposals yet for this project.</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {proposals.map(proposal => (
            <li
              key={proposal.id}
              style={{
                border: "1px solid #ccc",
                padding: "10px",
                borderRadius: "5px",
                marginBottom: "15px"
              }}
            >
              <p><strong>Freelancer ID:</strong> {proposal.freelancerId}</p>
              <p><strong>Bid Amount:</strong> {proposal.bidAmount}</p>
              <p><strong>Status:</strong> {proposal.status}</p>
              {proposal.status === "PENDING" && (
                <>
                  <button
                    onClick={() => updateProposalStatus(proposal.id, "ACCEPTED")}
                    style={{ marginRight: "10px", padding: "5px 10px" }}
                  >
                    Accept
                  </button>
                  <button
                    onClick={() => updateProposalStatus(proposal.id, "REJECTED")}
                    style={{ padding: "5px 10px" }}
                  >
                    Reject
                  </button>
                </>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
