import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const role = (localStorage.getItem("role") || "").toUpperCase();

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("userId");
    navigate("/login");
  };

  return (
    <nav style={{ background: "#222", padding: "10px", color: "#fff" }}>
      {role === "ADMIN" && (
        <>
          <Link to="/manage-users" style={{ marginRight: "10px" }}>Admin Dashboard</Link>
        </>
      )}

      {role === "CLIENT" && (
        <>
          <Link to="/dashboard" style={{ marginRight: "10px" }}>Client Dashboard</Link>
          <Link to="/post-project" style={{ marginRight: "10px" }}>Post Project</Link>
        </>
      )}

      {role === "FREELANCER" && (
        <>
          <Link to="/freelancer-dashboard" style={{ marginRight: "10px" }}>Freelancer Dashboard</Link>
          <Link to="/browse-projects" style={{ marginRight: "10px" }}>Browse Projects</Link>
        </>
      )}

      <button
        onClick={logout}
        style={{ background: "red", color: "#fff", border: "none", padding: "5px 10px" }}
      >
        Logout
      </button>
    </nav>
  );
}
