import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import PostProject from "./pages/PostProject";
import BrowseProjects from "./pages/BrowseProjects";
import ManageUsers from "./pages/ManageUsers";
import FreelancerDashboard from "./pages/FreelancerDashboard";

function App() {
  const token = localStorage.getItem("token");
  const role = localStorage.getItem("role");

  return (
    <Router>
      {token && <Navbar />}
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Protected routes */}
        <Route path="/dashboard" element={
          token && role === "CLIENT" ? <Dashboard /> : <Navigate to="/login" />
        } />
        <Route path="/post-project" element={
          token && role === "CLIENT" ? <PostProject /> : <Navigate to="/login" />
        } />
        <Route path="/browse-projects" element={
          token && role === "FREELANCER" ? <BrowseProjects /> : <Navigate to="/login" />
        } />
        <Route path="/freelancer-dashboard" element={
          token && role === "FREELANCER" ? <FreelancerDashboard /> : <Navigate to="/login" />
        } />
        <Route path="/manage-users" element={
          token && role === "ADMIN" ? <ManageUsers /> : <Navigate to="/login" />
        } />
      </Routes>
    </Router>
  );
}

export default App;
