import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

export default function FileUpload() {
  const { projectId, milestoneId } = useParams();
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [message, setMessage] = useState("");
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [contract, setContract] = useState(null);

  // Fetch uploaded files
  useEffect(() => {
    fetchFiles();
    fetchContractStatus();
  }, [projectId]);

  const fetchFiles = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8080/files/project/${projectId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUploadedFiles(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchContractStatus = async () => {
    try {
      const res = await axios.get(
        `http://localhost:8080/contracts/project/${projectId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setContract(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file) {
      setMessage("Please choose a file first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("projectId", projectId);

    setUploading(true);
    setMessage("");

    try {
      const res = await axios.post(
        `http://localhost:8080/files/upload`,
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        }
      );

      setMessage(`File "${res.data.fileName}" uploaded successfully!`);
      setFile(null);
      fetchFiles();
    } catch (err) {
      console.error(err);
      setMessage("Failed to upload file.");
    } finally {
      setUploading(false);
    }
  };

  const handleDelete = async (fileId) => {
    if (!window.confirm("Are you sure you want to delete this file?")) return;

    try {
      await axios.delete(`http://localhost:8080/files/${fileId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchFiles();
    } catch (err) {
      console.error(err);
      alert("Failed to delete file.");
    }
  };

  const getStatusBadgeColor = (status) => {
    switch (status?.toLowerCase()) {
      case "completed":
        return "#28a745"; // green
      case "pending":
        return "#ffc107"; // yellow
      case "ongoing":
        return "#17a2b8"; // blue
      default:
        return "#6c757d"; // gray
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Upload File for Project {projectId}</h2>

      {/* Contract Status */}
      {contract ? (
        <div style={{ marginBottom: "15px" }}>
          <span
            style={{
              padding: "5px 10px",
              borderRadius: "12px",
              backgroundColor: getStatusBadgeColor(contract.status),
              color: "white",
              fontWeight: "bold",
            }}
          >
            {contract.status}
          </span>
        </div>
      ) : (
        <p style={{ color: "red" }}>No active contract for this project.</p>
      )}

      {/* Upload Form */}
      <form onSubmit={handleUpload}>
        <input type="file" onChange={handleFileChange} />
        <br />
        <button
          type="submit"
          disabled={uploading}
          style={{
            marginTop: "10px",
            padding: "5px 10px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
        >
          {uploading ? "Uploading..." : "Upload"}
        </button>
      </form>

      {message && <p style={{ marginTop: "10px" }}>{message}</p>}

      {/* Uploaded Files List */}
      <h3 style={{ marginTop: "20px" }}>Uploaded Files</h3>
      {uploadedFiles.length > 0 ? (
        <ul>
          {uploadedFiles.map((f) => (
            <li key={f.id} style={{ marginBottom: "8px" }}>
              <a
                href={`http://localhost:8080/files/${f.id}/download`}
                target="_blank"
                rel="noopener noreferrer"
              >
                {f.fileName}
              </a>
              <button
                onClick={() => handleDelete(f.id)}
                style={{
                  marginLeft: "10px",
                  backgroundColor: "red",
                  color: "white",
                  border: "none",
                  padding: "3px 7px",
                  borderRadius: "4px",
                  cursor: "pointer",
                }}
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No files uploaded yet.</p>
      )}
    </div>
  );
}
