// src/components/Navbar.js
import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const role = (localStorage.getItem("role") || "").toUpperCase();

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("userId");
    navigate("/login");
  };

  const navStyle = {
    background: "#1f2937", // matches Header gradient palette
    padding: "10px 20px",
    color: "#fff",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    boxShadow: "0 3px 8px rgba(0,0,0,0.25)",
    borderBottomLeftRadius: "8px",
    borderBottomRightRadius: "8px",
  };

  const linkContainerStyle = {
    display: "flex",
    alignItems: "center",
    gap: "15px",
  };

  const linkStyle = {
    color: "#f1f5f9",
    textDecoration: "none",
    padding: "6px 12px",
    borderRadius: "6px",
    transition: "all 0.3s ease",
    fontWeight: "500",
  };

  const logoutButtonStyle = {
    background: "#ef4444",
    color: "#fff",
    border: "none",
    padding: "6px 14px",
    borderRadius: "6px",
    cursor: "pointer",
    transition: "all 0.3s ease",
    fontWeight: "500",
  };

  const hoverLinkStyle = {
    background: "#3b82f6",
    color: "#fff",
  };

  return (
    <nav style={navStyle}>
      <div style={linkContainerStyle}>
        {role === "ADMIN" && (
          <Link
            to="/manage-users"
            style={linkStyle}
            onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
            onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
          >
            Admin Dashboard
          </Link>
        )}

        {role === "CLIENT" && (
          <>
            <Link
              to="/dashboard"
              style={linkStyle}
              onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
              onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
            >
              Client Dashboard
            </Link>
            <Link
              to="/post-project"
              style={linkStyle}
              onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
              onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
            >
              Post Project
            </Link>
            <Link
              to="/contracts"
              style={linkStyle}
              onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
              onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
            >
              Contracts
            </Link>
          </>
        )}

        {role === "FREELANCER" && (
          <>
            <Link
              to="/freelancer-dashboard"
              style={linkStyle}
              onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
              onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
            >
              Freelancer Dashboard
            </Link>
            <Link
              to="/browse-projects"
              style={linkStyle}
              onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
              onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
            >
              Browse Projects
            </Link>
            <Link
              to="/contracts"
              style={linkStyle}
              onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
              onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
            >
              Contracts
            </Link>
          </>
        )}

        <Link
          to="/profile"
          style={linkStyle}
          onMouseOver={(e) => Object.assign(e.currentTarget.style, hoverLinkStyle)}
          onMouseOut={(e) => Object.assign(e.currentTarget.style, linkStyle)}
        >
          Profile
        </Link>
      </div>

      <button
        onClick={logout}
        style={logoutButtonStyle}
        onMouseOver={(e) => (e.currentTarget.style.background = "#dc2626")}
        onMouseOut={(e) => (e.currentTarget.style.background = "#ef4444")}
      >
        Logout
      </button>
    </nav>
  );
}
