import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

export default function Milestones() {
  const { projectId } = useParams();
  const [milestones, setMilestones] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [description, setDescription] = useState("");
  const [status, setStatus] = useState("PENDING");
  const [contractId, setContractId] = useState(null); // fetched from API
  const token = localStorage.getItem("token");

  // Fetch milestones
  useEffect(() => {
    axios
      .get(`http://localhost:8080/milestones/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => setMilestones(res.data))
      .catch((err) => console.error(err));
  }, [projectId, token]);

  // Fetch contractId for the project
  useEffect(() => {
    axios
      .get(`http://localhost:8080/contracts/project/${projectId}`, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then((res) => {
        if (res.data && res.data.id) {
          setContractId(res.data.id);
        }
      })
      .catch((err) => console.error(err));
  }, [projectId, token]);

  const handleAddMilestone = (e) => {
    e.preventDefault();
    if (!contractId) {
      alert("No contract found for this project.");
      return;
    }

    axios
      .post(
        "http://localhost:8080/milestones",
        {
          contractId,
          description,
          status,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      )
      .then((res) => {
        setMilestones([...milestones, res.data]);
        setDescription("");
        setStatus("PENDING");
        setShowForm(false);
      })
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Milestones for Project {projectId}</h2>

      <button
        onClick={() => setShowForm(!showForm)}
        style={{ marginBottom: "15px", padding: "5px 10px" }}
      >
        {showForm ? "Cancel" : "Add Milestone"}
      </button>

      {showForm && (
        <form
          onSubmit={handleAddMilestone}
          style={{
            marginBottom: "20px",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "5px",
          }}
        >
          <div style={{ marginBottom: "10px" }}>
            <label>Description:</label>
            <br />
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              style={{ width: "100%", height: "60px" }}
            />
          </div>
          <div style={{ marginBottom: "10px" }}>
            <label>Status:</label>
            <br />
            <select
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              style={{ width: "100%" }}
            >
              <option value="PENDING">Pending</option>
              <option value="COMPLETED">Completed</option>
            </select>
          </div>
          <button type="submit" style={{ padding: "5px 10px" }}>
            Save Milestone
          </button>
        </form>
      )}

      {milestones.length === 0 ? (
        <p>No milestones yet.</p>
      ) : (
        <ul>
          {milestones.map((m) => (
            <li key={m.id}>
              {m.description} — <strong>{m.status}</strong>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
