import React, { useEffect, useState } from "react";
import axios from "axios";

export default function ManageUsers() {
  const [clients, setClients] = useState([]);
  const [freelancers, setFreelancers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const res = await axios.get("http://localhost:8080/api/auth/users");
        const allUsers = res.data;

        // Separate clients and freelancers
        setClients(allUsers.filter(user => user.role.toUpperCase() === "CLIENT"));
        setFreelancers(allUsers.filter(user => user.role.toUpperCase() === "FREELANCER"));
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUsers();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h1>Clients</h1>
      {clients.length === 0 ? <p>No clients found.</p> :
        <ul>
          {clients.map(client => (
            <li key={client.id}>{client.name} ({client.email})</li>
          ))}
        </ul>
      }

      <h1>Freelancers</h1>
      {freelancers.length === 0 ? <p>No freelancers found.</p> :
        <ul>
          {freelancers.map(freelancer => (
            <li key={freelancer.id}>{freelancer.name} ({freelancer.email})</li>
          ))}
        </ul>
      }
    </div>
  );
}
