import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const role = (localStorage.getItem("role") || "").toUpperCase();

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("userId");
    navigate("/login");
  };

  const navStyle = {
    background: "#1f1f1f",
    padding: "12px 20px",
    color: "#fff",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    boxShadow: "0 2px 8px rgba(0,0,0,0.2)",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    position: "sticky",
    top: 0,
    zIndex: 1000
  };

  const linkContainerStyle = {
    display: "flex",
    alignItems: "center",
    gap: "15px"
  };

  const linkStyle = {
    color: "#fff",
    textDecoration: "none",
    padding: "6px 12px",
    borderRadius: "6px",
    transition: "background 0.3s",
  };

  const logoutButtonStyle = {
    background: "#e74c3c",
    color: "#fff",
    border: "none",
    padding: "6px 12px",
    borderRadius: "6px",
    cursor: "pointer",
    transition: "background 0.3s",
  };

  return (
    <nav style={navStyle}>
      <div style={linkContainerStyle}>
        {role === "ADMIN" && (
          <Link
            to="/manage-users"
            style={{ ...linkStyle, hover: { background: "#333" } }}
          >
            Admin Dashboard
          </Link>
        )}

        {role === "CLIENT" && (
          <>
            <Link to="/dashboard" style={linkStyle}>Client Dashboard</Link>
            <Link to="/post-project" style={linkStyle}>Post Project</Link>
            <Link to="/contracts" style={linkStyle}>Contracts</Link>
          </>
        )}

        {role === "FREELANCER" && (
          <>
            <Link to="/freelancer-dashboard" style={linkStyle}>Freelancer Dashboard</Link>
            <Link to="/browse-projects" style={linkStyle}>Browse Projects</Link>
            <Link to="/contracts" style={linkStyle}>Contracts</Link>
          </>
        )}
         <Link to="/profile" style={linkStyle}>Profile</Link>
      </div>

      <button
        onClick={logout}
        style={logoutButtonStyle}
        onMouseOver={(e) => e.currentTarget.style.background = "#c0392b"}
        onMouseOut={(e) => e.currentTarget.style.background = "#e74c3c"}
      >
        Logout
      </button>
    </nav>
  );
}
