import React from "react";

export default function Header() {
  const headerStyle = {
    background: "#0f172a",       // dark-blue background
    color: "#e0e7ff",           // soft text color
    padding: "25px 20px",
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "700",
    letterSpacing: "1px",
    position: "relative",
    overflow: "hidden",
    borderBottom: "3px solid #3b82f6",
  };

  const taglineStyle = {
    fontSize: "16px",
    fontWeight: "400",
    marginTop: "6px",
    color: "#c7d2fe",
    opacity: 0,
    animation: "fadeIn 1s forwards 0.5s",
  };

  return (
    <header style={headerStyle}>
      Freelance Connect
      <div style={taglineStyle}>
        Your gateway to opportunities & talent
      </div>

      <style>
        {`
          @keyframes fadeIn {
            to { opacity: 1; }
          }
        `}
      </style>
    </header>
  );
}
