import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const role = (localStorage.getItem("role") || "").toUpperCase();

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("userId");
    navigate("/login");
  };

  const navStyle = {
    background: "#1e293b",       // slightly lighter dark background
    padding: "12px 25px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  };

  const linkContainerStyle = {
    display: "flex",
    gap: "18px",
  };

  const linkStyle = {
    color: "#e0e7ff",
    textDecoration: "none",
    padding: "8px 15px",
    borderRadius: "8px",
    fontWeight: "500",
    transition: "all 0.3s ease",
  };

  const logoutStyle = {
    background: "#3b82f6",
    color: "#fff",
    border: "none",
    padding: "8px 18px",
    borderRadius: "8px",
    fontWeight: "500",
    cursor: "pointer",
    transition: "all 0.3s ease",
  };

  const hoverEffect = (e, isButton = false) => {
    if (isButton) {
      e.currentTarget.style.background = "#2563eb";
      e.currentTarget.style.transform = "scale(1.05)";
      e.currentTarget.style.boxShadow = "0 4px 12px rgba(37,99,235,0.5)";
    } else {
      e.currentTarget.style.background = "#334155";
      e.currentTarget.style.transform = "scale(1.05)";
    }
  };

  const resetEffect = (e, isButton = false) => {
    if (isButton) {
      e.currentTarget.style.background = "#3b82f6";
      e.currentTarget.style.transform = "scale(1)";
      e.currentTarget.style.boxShadow = "none";
    } else {
      e.currentTarget.style.background = "transparent";
      e.currentTarget.style.transform = "scale(1)";
    }
  };

  return (
    <nav style={navStyle}>
      <div style={linkContainerStyle}>
        {role === "ADMIN" && (
          <Link
            to="/manage-users"
            style={linkStyle}
            onMouseOver={hoverEffect}
            onMouseOut={resetEffect}
          >
            Admin Dashboard
          </Link>
        )}

        {role === "CLIENT" && (
          <>
            <Link
              to="/dashboard"
              style={linkStyle}
              onMouseOver={hoverEffect}
              onMouseOut={resetEffect}
            >
              Dashboard
            </Link>
            <Link
              to="/post-project"
              style={linkStyle}
              onMouseOver={hoverEffect}
              onMouseOut={resetEffect}
            >
              Post Project
            </Link>
            <Link
              to="/contracts"
              style={linkStyle}
              onMouseOver={hoverEffect}
              onMouseOut={resetEffect}
            >
              Contracts
            </Link>
          </>
        )}

        {role === "FREELANCER" && (
          <>
            <Link
              to="/freelancer-dashboard"
              style={linkStyle}
              onMouseOver={hoverEffect}
              onMouseOut={resetEffect}
            >
              Dashboard
            </Link>
            <Link
              to="/browse-projects"
              style={linkStyle}
              onMouseOver={hoverEffect}
              onMouseOut={resetEffect}
            >
              Browse Projects
            </Link>
            <Link
              to="/contracts"
              style={linkStyle}
              onMouseOver={hoverEffect}
              onMouseOut={resetEffect}
            >
              Contracts
            </Link>
          </>
        )}

        <Link
          to="/profile"
          style={linkStyle}
          onMouseOver={hoverEffect}
          onMouseOut={resetEffect}
        >
          Profile
        </Link>
      </div>

      <button
        onClick={logout}
        style={logoutStyle}
        onMouseOver={(e) => hoverEffect(e, true)}
        onMouseOut={(e) => resetEffect(e, true)}
      >
        Logout
      </button>
    </nav>
  );
}
